import logo from './logo.png';
import './App.css';
import React, { useEffect, useState } from 'react';

function App() {
  const [amount, setAmount] = useState(0);
  const [installments, setInstallments] = useState([]);

  /**
    * This function calculates the installments for a purchase plan.
    * Assume there are always four installments.
    * The function need not return anything but should be used to display
    * the four installments back to the user after the user inputs a valid
    * dollar amount and clicks submit.
    * @param {number} amount the amount the customer intends to purchase
  */
  const createPaymentPlan = amount => {
    console.log("Creating payment plan with amount: $", amount);
    const amountInCents = amount * 100;
    const amountInCentsRemainder = amountInCents % 4;
    console.log(amountInCents - amountInCentsRemainder);
    const evenFour = (amountInCents - amountInCentsRemainder) / 4;
    console.log({ amountInCents, amountInCentsRemainder, evenFour });
    let inst = [];
    for (let i = 3; i >= 0; i--) {
      console.log(i, amountInCentsRemainder)
      let amount = evenFour;
      if (amountInCentsRemainder < i) amount++;
      inst.push({ amount: amount / 100 })
    }
    console.log('installments', inst);
  }

  useEffect(() => {
    // for testing purposes, a purchase amount of $100 should calculate four
    // installments of $25
    createPaymentPlan(amount);
  }, [amount])

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <form>
          <label>How much do you plan to spend?</label>
          <input type="text" id="amount" value={amount} onChange={e => setAmount(e.target.value)} />
          <input type="submit" value="Submit" />
        </form>
      </header>
    </div>
  );
}

export default App;
